        const subStep = t % stepSize;
        if (t > 9*stepSize) {
            t = 0;
        }

        if (subStep > stepSize/10 && subStep <= stepSize/2) {
            // Scale the neuron from 0 to 0.5 with a sigmoid function
            neuronScale = 0.5 * (1 - Math.exp(-0.1 * (subStep - 10)));
        } 
        if (subStep > stepSize/2 && subStep <= stepSize) {
            // Scale the neuron from 0.5 to 0 with a sigmoid function
            neuronScale = 0.5 * (1 - Math.exp(-0.1 * (stepSize - subStep)));
        } 

        if (subStep == 0) {
            const index = Math.floor(t / stepSize);
            matrix = tensor[index];
            allocated = allocation[index];
            serverSizes = getServerSizes(allocated);
        }
	});

</script>

<T.PerspectiveCamera makeDefault position={[10, 10, 10]} fov={24}>
    <OrbitControls maxPolarAngle={degToRad(80)} enableZoom={false} target={{ y: 0.5 }} />
</T.PerspectiveCamera>

