<T.Group>
    {#each {length: n_servers} as _, i}
        {#each {length: n_users} as _, j}
            <T.Mesh scale={0.3 + (matrix[i][j] ? neuronScale : 0)} position={[0.2*i - 0.1*n_servers, 0.5, 0.2*j - 0.1*n_users]} castShadow let:ref>
                <T.BoxGeometry args={[0.1, 0.1, 0.1]} />
                <T.MeshStandardMaterial color={serverColors[allocated[j]]} />
            </T.Mesh>
        {/each}
        <T.Mesh scale={0.01 + 1.5*serverSizes[i]} position={[0.2*i - 0.1*n_servers, 0.5, + 0.1*n_users + 0.25]} castShadow let:ref>
            <T.BoxGeometry args={[0.5, 0.5, 0.5]} />
            <T.MeshStandardMaterial color = {serverColors[i]}/>
        </T.Mesh>
    {/each}
